import React, { useRef } from "react";
import emailjs from "@emailjs/browser";
import { useForm } from "react-hook-form"
import Modal from './modal/Modal'
import useModal from "./modal/useModal"
import "./App.css";

export const App = () => {
  const { register, formState, handleSubmit } = useForm()
  const { isOpen, openModal, closeModal } = useModal()
  const form = useRef()
  const sendEmail = (e) => {
    e.preventDefault();

    emailjs.sendForm(
      process.env.REACT_APP_EMAILJS_SERVICE_ID, process.env.REACT_APP_EMAILJS_TEMPLATE_ID,
      form.current,
      process.env.REACT_APP_EMAILJS_PUBLIC_KEY)
      .then(
      (result) => {
        console.log(result.text);
      },
      (error) => {
        console.log(error.text);
      }
    );
  };

  return (
    // <div className="container">
    //   <form ref={form} onSubmit={sendEmail} className="form">
    //     <label>Name</label>
    //     <input type="text" name="name" />
    //     <label>Email</label>
    //     <input type="email" name="email" />
    //     <label>Message</label>
    //     <textarea name="message" />
    //     <button type="submit">Envoyer</button>
    //   </form>
    // </div>
    <div className="container">
      <form ref={form} onSubmit={sendEmail} className="form">
        <label htmlFor="firstName">firstname</label>
        <input
          type="text"
          name="firstname"
          id="firstName"
            {...register("firstName", {
              required: true,
              minLength: 2,
              maxLength: 20,
              pattern: /^[a-zA-Z\s\-À-ÖØ-öø-ÿ']+$/g,
            })}
            className="text-black p-2 mb-4"
        />
        {/* firstName validation by react-hook-form */}
        <div className="text-white bg-red-700 -mt-4">
            {errors?.firstName?.type === "required" && (
              <p>
                Ce champs doit être rempli
              </p>
            )}
            {errors?.firstName?.type === "maxLength" && (
              <p>
                Ne doit pas excéder 20 caractères
              </p>
            )}
            {errors?.firstName?.type === "minLength" && (
              <p>
                Il doit y avoir moins 2 caractères
              </p>
            )}
            {errors?.firstName?.type === "pattern" && (
              <p >
                Caractères alphabétiques uniquement
              </p>
            )}
        </div>


        <label htmlFor="lastName">lastname</label>
        <input type="text" name="lastname" />

        <label htmlFor="email">Email</label>
        <input type="email" name="email" />

        <label>Message</label>
        <textarea name="message" />

        <button type="submit">Envoyer</button>
      </form>
    </div>
  );
};

export default App;
